	('79c204c8-e9eb-4d30-ad4a-cb5e3cd133c0', '90 FR 8433', '2025-02003', 8437, 'https://www.federalregister.gov/documents/2025/01/29/2025-02003/declaring-a-national-energy-emergency', 'https://www.govinfo.gov/content/pkg/FR-2025-01-29/pdf/2025-02003.pdf', 'Presidential Document', 'Executive Order', '2025-01-29', '2025-01-20', 8433, 'Declaring a National Energy Emergency', NULL, '14156', NULL, 'https://www.federalregister.gov/documents/full_text/xml/2025/01/29/2025-02003.xml', 'https://www.federalregister.gov/documents/full_text/html/2025/01/29/2025-02003.html', 'https://www.federalregister.gov/api/v1/documents/2025-02003?publication_date=2025-01-29', '<PRESDOCU>
                <EXECORD>
                    <TITLE3>Title 3—</TITLE3>
